void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord -.5*iResolution.xy)/iResolution.y;

    float xmod = .4*sin(iTime*25.)*uv.x;
    float ymod = .4*sin(iTime*25.)*uv.y;
    //vec2 d = vec2(vec2(.5,.5) - length((xmod,ymod)));
    vec2 d = vec2(vec2(.5,.5) - length((uv)));

    d *= 6.;
    vec3 col = vec3(d * vec2(8.9,1.9), .2);

    if(iMouse.z > 0.001){
        col *= sin(iTime)*cos(iTime)+ymod;
        col.rb *= .1;
    }
    else 
    {
        col *= sin(iTime)*cos(iTime)+xmod;
    }

    fragColor = vec4(col,1.0);
}
