void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2.0/uv.y,4));
 vec3 col2 = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2.0,4.0/uv.y));
 vec3 col3 = 0.5 + 0.5*cos(iTime+uv.xyy+vec3(0,2.0,4.0/uv.x));
vec3 col4 = mix(col,col2,cos(iTime));



 vec3 col10 = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2.0,4.0/uv.y));
 vec3 col11 = 0.5 + 0.5*cos(iTime+uv.xyy+vec3(4.0/uv.x,2.0/uv.y,4.0/uv.y));


//vec3 col5 = cos(col);//smoothstep(col,col2,col4);

vec3 col5 = cos(smoothstep(col+col3/333.0,col2+col/0.02,col4/0.02))/4.5 +col/ 5.0 -mix(col3,col2,cos(iTime));//;
col5 = col5/col10 + col11/col10;

    // Output to screen
    fragColor = vec4(col5,1.0);
}
