int Wav=1;

float wave(float A,float k,float omega,float phi,vec2 U){
return A*sin(k*U.x-omega*iTime+phi);}

void mainImage( out vec4 O,vec2 u )
{
    vec2 R=iResolution.xy,
         U = 100.*( u - .5*R ) / R.x;

    float w=0.;
    for(float j=1.;j<30.;j++){
    if(Wav==0){
        w+=wave(3.0/(2.*j-1.),0.3*(2.*j-1.),0.8*(2.*j-1.),0.,U);}
    if(Wav==1){
        w+=wave(3.0/j/j*sin(j/2.*3.14),0.3*j,0.8*j,0.,U);}}
    O+=step(abs(0.2*U.y-w),0.05);
}
