float sepAmt = 0.02;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    float sep = sin(iTime)*sepAmt/2.0+sepAmt/2.0;

    float sa = 0.0;
    float r = texture(iChannel0, ((uv*2.0-1.0)*(1.0-sa)+1.0)*.5).r;
    sa = sa + sep;
    float g = texture(iChannel0, ((uv*2.0-1.0)*(1.0-sa)+1.0)*.5).g;
    sa = sa + sep;
    float b = texture(iChannel0, ((uv*2.0-1.0)*(1.0-sa)+1.0)*.5).b;

    // Output to screen
    fragColor = vec4(r,g,b,1.0);
}
