float PI = 3.14159;

mat2 rotate2d(float _angle){
    return mat2(cos(_angle),-sin(_angle),
                sin(_angle),cos(_angle));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float p = 4.5;
    float m = 1. + 10. * 0.5 * (1. + sin(iTime * 0.3));
    float m2 = 1. + 10. * 0.5 * (1. + sin(iTime * 0.3));
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;


    vec2 uv2 = abs(fract(rotate2d(iTime * 0.13) * uv * m) - 0.5) / 0.5;
    uv2.x += 0.1 * sin(2. * PI * uv.y + iTime * 1.7);
    vec2 uvB = rotate2d(iTime * 0.1) * uv;
    uvB.y += 0.03 * sin(PI * uvB.x + iTime * 1.3);
    //uvB.x += 0.1 * sin(2. * PI * uvB.y + iTime * 1.3);
    vec2 uv3 = abs(fract(uvB * m2) - 0.5) / 0.5;
    uv3.y += 0.1 * sin(2. * PI * uvB.x + iTime * 1.3);
    uv3.x += 0.1 * sin(2. * PI * uvB.t + iTime * 1.1);

    float f = max(uv2.x, uv2.y);
    float tk = 0.7;
    float ff = f < tk ? 0. : pow((f - tk) / (1. - tk), p);
    vec3 col = vec3(ff * 0.6, ff * 0.7, ff * 0.9);

    float f2 = max(uv3.x, uv3.y);
    float tk2 = tk;
    float ff2 = f2 < tk2 ? 0. : pow((f2 - tk2) / (1. - tk2), p);
    vec3 col2 = vec3(ff2 * 0.6, ff2 * 0.7, ff2 * 0.9);

    fragColor = vec4(col + col2,1.0);
}
