// variant of https://shadertoy.com/view/7ldcWn


#define B   O +=   smoothstep( 12./R.y, 0., abs(length(U)-.9) -.07 )   \
                 * ( .5 + .5* cos(30.*(length(U)-.9)) - O );           \
/*               * vec4(.7,.7,1,1);  /* nice transp col variant :-) */ \
            U += D

void mainImage( out vec4 O, vec2 u )
{
    vec2 R = iResolution.xy, D = vec2(.5,-.5),
         U = fract( ( 4.*u - R-R ) / R.y ) - .5; // tile = 1/4 circle
    O-=O  -.4;                                   //  3-grey variant
    U = abs( U.x*U.y<0. ? U.yx : U )             // tile = 1/16 circle
    - D; B
    +.5; B
    -.5; B;  
}
