void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float numLines = 150.0f;
    vec2 uv = fragCoord/iResolution.xy;
    vec4 color = texture(iChannel0, uv);
    float even = mod((uv.y * numLines), 2.0f);
    vec4 clr = vec4(1.5f, 1.5f, 1.5f, 1.0f);
    if (even < 1.0f) {
      clr = vec4(0.5f, 0.5f, 0.5f, 1.0f);
    }
    fragColor = clr * color;
}
