#define RANGE .25
#define PINCH_VECTOR vec2(sin(iTime*10.),cos(iTime*20.))*.03
#define PINCH_VECTOR_CONST vec2(1,1)*.1

vec2 pinch(vec2 uv,vec2 targetPoint,vec2 vector,float range)
{
    float dist = distance(uv,targetPoint);
    vec2 falloff = smoothstep(0.,1.,dist/range)*vector;
    return uv-vector+falloff;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates-[0,1]
    vec2 uv = fragCoord/iResolution.y;
    vec2 mouse = iMouse.xy/iResolution.y;

    if(iMouse.xy == vec2(0))
    {
        mouse = vec2(0.5);
        mouse.x *= iResolution.x/iResolution.y;
    }
    uv = pinch(uv,mouse,PINCH_VECTOR,RANGE);

    vec4 color = texture(iChannel0,uv);
    fragColor = color;
}
