float smoot(float x) {
    return -2.0*x*x*x+3.0*x*x;
}
float lerp(float a, float b, float t) {
    return a+(b-a)*t;
}
float frac(float x) {
    return x-floor(x);
}
float random(vec2 uv) {
    return frac(sin(dot(uv,vec2(12.9898,78.233)))*43758.5453123);
}
float noise(float x) {
    float xInEdge = floor(x);

    float anchor0 = random(vec2(xInEdge,0));
    float anchor1 = random(vec2(xInEdge+1.0,0));

    return lerp(anchor0,anchor1,smoot(frac(x)));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;


    fragColor = vec4(noise(uv.x+iTime));
    fragColor = vec4(noise(uv.x+(uv.y)*iResolution.x/20.0+iTime));
}
