void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv -= .5;
    uv = abs(uv);
    float t = iTime*.33;

    uv *= sin(t*.25)*4.;
    // Time varying pixel color


    // Output to screen
    fragColor = vec4(
        fract(t+distance(uv.x,abs(uv.y))*3.),
        fract(-.43*t+distance(sin(uv.x*2.+t),abs(uv.y))*1.3),
        fract(-.8*t+distance(uv.x,abs(uv.y))*2.1),
        1.
    );
}
