void mainImage( out vec4 o, vec2 P)
{
    o-=o;
    for(int i=0; i++<9;)
    {
        vec2 p = P+vec2(i%3,i/3)/3.-iResolution.xy*.5;
        float l = length(p)/8e1,
        d = ceil(l+=sin(l)*.5),
        a = d-l-.5;
        o += fract(texture(iChannel0,vec2(mod(ceil(atan(p.y,p.x)*d/1.57+
        sin(d+iTime)*sqrt(d)),d*4.),d)/9.).r+vec4(0,.1,.3,0))*sqrt(1.-a*a/.3)/9.;
    }
}

