Main{
    Q = vec4(1.1,1.2,1.3,1.4)*B(U)/float(iFrame);

    //Q = .5+.5*A(U);
}

#define R iResolution.xy
#define A(U) texture(iChannel0,(U)/R)
#define B(U) texture(iChannel1,(U)/R)
#define C(U) texture(iChannel2,(U)/R)
#define D(U) texture(iChannel3,(U)/R)
#define Main void mainImage(out vec4 Q, in vec2 U)


vec4 hash44(vec4 p4)
{ // Dave H
	p4 = fract(p4  * vec4(.1031, .1030, .0973, .1099));
    p4 += dot(p4, p4.wzxy+33.33);
    return fract((p4.xxyz+p4.yzzw)*p4.zywx);
}

Main{
    Q = vec4(0);
    float r; vec2 v;
    U = 2.*(U-.5*R)/R.y;

    v = U-vec2(-.1,.2);
    r = length(v)-.4;
    Q += exp(-20.*r*r)*sin(40.*r*dot(v,v));

    v = U-vec2(.3,-.2);
    r = length(v)-.6;
    Q += .5*exp(-20.*r*r)*sin(40.*r*dot(v,v));


    v = U-vec2(-.8,-.3);
    r = length(v)-.2;
    Q += exp(-40.*r*r)*cos(140.*r*dot(v,v));

    v = U-vec2(1.,.5);
    r = length(v)-1.;
    Q += .1*exp(-20.*r*r)*sin(20.*r*dot(v,v));

    v = U-vec2(0,-.5);
    r = length(v)-1.4;
    Q += .06*exp(-20.*r*r)*sin(20.*r*dot(v,v));
}

Main {
    Q = B(U);
    for (float i = 0.; i < 10.; i++) {
        vec4 h = hash44(vec4(U,iTime,i));
        vec2 r = .04*R.x*(h.xy*2.-1.);
        vec4 n = A(U+r+vec2(0,1)),
             e = A(U+r+vec2(1,0)),
             s = A(U+r-vec2(0,1)),
             w = A(U+r-vec2(1,0));
        vec2 g = (1.+h.z)*R.x*vec2(e.w-w.w,n.w-s.w);

        Q += .05*R.x*exp(-length(r-g))*vec4(.5-.5*sin(5.*h.z+vec3(1,2,3)),1);
    }
}
