void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 Id = (fragCoord - fragCoord.xy * .5) / iResolution.y * 20.;
    vec2 uv = fract(Id) - .5;
    Id = floor(Id);

    float MaxX = floor(iResolution.x / iResolution.y * 10.);
    vec2 PointerAt = vec2(
        mod(floor(iTime * 3. * (cos(iTime * 2.24326) * .001 + .999) + cos(iTime) + cos(iTime * 1.35325)), MaxX),
        mod(floor(iTime / MaxX * 10. * (cos(iTime * 3.4423) * .001 + .999) + cos(iTime) + cos(iTime * 2.34555)), 10.)
    );

    float Brightness = 0.;

    if (PointerAt == Id) Brightness = 1.;

    uv = (uv * iResolution.y + .5 * iResolution.xy) / iResolution.xy;
    Brightness = clamp(Brightness + texture(iChannel0, uv).x * (1. - length(Id - PointerAt) * .05) * .8, 0., 1.);
    Brightness *= 1. - length(Id * .05 - .5);
    fragColor = vec4(vec3(Brightness), 1.);
}
