float RADIUS = 12.0;
float size = 3000.0;
float PI = 3.14159;

// PDF
float gaussianBlur (float sigma, float x, float y) {
    return exp(-(x * x + y * y) / (2.0 * sigma * sigma)) / (2.0 * sigma * sigma * PI); // fixed by zznewclear13, 2021-12-03 [InternetShortcut] URL=https://www.shadertoy.com/user/zznewclear13

}

vec4 computedBlur (float gauss, vec4 inputColor) {
        return (inputColor * gauss);
}

vec4 blurColor (vec2 uv) {
    vec4 color = vec4(0.0);
    float sum = 0.0;
    for (float r = -RADIUS; r <= RADIUS; r++) {
        for (float c = -RADIUS; c <= RADIUS; c++) {
             vec2 target = uv + vec2(r / iResolution.x, c / iResolution.y); // fixed by zznewclear13, 2021-12-03 [InternetShortcut] URL=https://www.shadertoy.com/user/zznewclear13
            float gauss = gaussianBlur(RADIUS / 3.0, r, c);
            color += computedBlur(gauss, texture(iChannel0, target));
            sum += gauss;
        }
    }

    color /= sum;
    return color;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord ) 
{
    vec2 uv = fragCoord/iResolution.xy;

    //size = size * cos(iTime * .1);
    vec4 color = blurColor(uv);

    fragColor = color;
}
