void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    int iter = 100;
    int n = 0;
    vec2 c = uv;
    c -= 0.5f;
    c *= 4.f;
    vec2 z = c;
    while(n<iter)
    {
        float aa = z.x*z.x -z.y*z.y;
        float bb = 2.f*z.x*z.y;
        z = vec2(aa,bb) + c;
        if(z.x+z.y > 16.f)
        {
        break;
        }
        n++;
    }
    // Output to screen
    float bright = float(n)/float(iter);  
    vec2 col = bright*(normalize(z)+1.f)/2.f;
    fragColor.rgb = vec3(col.x,col.y,0.f);
     if(n == iter)
     fragColor.rgb = vec3(0.f);

}
