const int indexMatrix4x4[16] = int[](0,  8,  2,  10,
                                     12, 4,  14, 6,
                                     3,  11, 1,  9,
                                     15, 7,  13, 5);

float indexValue(vec2 uv) {
    int x = int(mod (uv.x, 4.0));
    int y = int(mod(uv.y, 4.0));
    return float(indexMatrix4x4[(x + y * 4)]) / 16.0;
}

float dither(float color, vec2 uv) {
    float closestColor = (color < 0.5) ? 0.0 : 1.0;
    float secondClosestColor = 1.0 - closestColor;
    float d = indexValue(uv);
    float distance = abs(closestColor - color);
    return (distance < d) ? closestColor : secondClosestColor;
}

void mainImage (out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = fragCoord.xy / iResolution.xy;
    vec4 text = texture(iChannel0, uv);
    fragColor = vec4(vec3(dither(text.r, fragCoord)), 1);

}
