const vec4 red = vec4( 198, 12 ,48, 255 ) / 255.0;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 res = iResolution.xy;
    vec2 p = ( 2.0 * fragCoord - res )/res.y;

	vec4 color = vec4( 1.0 );
	float xOffset = 0.5;
    //rays
    float r = cos( atan( p.y, p.x + xOffset ) * 16.0 )+ 0.5*sin((p.y + iTime) * 4.0) * 0.038 + 0.3*sin((p.y + iTime) * 8.0) * 0.01 + 0.05*sin((p.y + iTime) * 40.0) * 0.05; 
    float f = smoothstep( r, r + 0.1, length( p )*0.05 )+ 0.5*sin((p.y + iTime) * 4.0) * 0.038 + 0.3*sin((p.y + iTime) * 8.0) * 0.01 + 0.05*sin((p.y + iTime) * 40.0) * 0.05;
	color = mix( red, color, f ) + 0.5*sin((p.y + iTime) * 4.0) * 0.038 + 0.3*sin((p.y + iTime) * 8.0) * 0.01 + 0.05*sin((p.y + iTime) * 40.0) * 0.05;;
    //circle
    r = 0.6 + 0.5*sin((p.y + iTime) * 4.0) * 0.038 + 0.3*sin((p.y + iTime) * 8.0) * 0.01 + 0.05*sin((p.y + iTime) * 40.0) * 0.05;
    f = smoothstep( r, r + 0.01, length( p + vec2( xOffset, 0.0 ) ) ) + 0.5*sin((p.y + iTime) * 4.0) * 0.038 + 0.3*sin((p.y + iTime) * 8.0) * 0.01 + 0.05*sin((p.y + iTime) * 40.0) * 0.05;
    color = mix( red, color, f );

    fragColor = color;
}
