vec2 tile(vec2 uv, float zoom){
    uv *= zoom;
    return fract(uv);
}


float star(vec2 _uv){
    _uv = 2.*_uv-1.;
    vec2 pos = vec2(0.5)-_uv;
    float r = length(pos)*2.0;
    float a = atan(pos.y,pos.x);
    float f = abs(cos(a*12.)*sin(a*3.))*.8+.1;

    return 1.-smoothstep(f,f+0.02,r);
}

float starPattern(vec2 _uv) {
    return  star(_uv+vec2(0.,-0.5))+
            star(_uv+vec2(0.,0.5))+
            star(_uv+vec2(-0.5,0))+
            star(_uv+vec2(0.5,0.5));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord){
    vec2 uv = fragCoord/iResolution.xy;
    uv.x *= iResolution.x/iResolution.y;
    vec3 color = vec3(0.);


    vec2 grid1 = tile(uv,7.);
    grid1 = tile(uv + vec2(cos(iTime),sin(iTime))*0.01,7.);
    color += mix(vec3(0.085,0.214,0.375),vec3(0.893,0.953,0.106),starPattern(grid1));

    vec2 grid2 = tile(uv,3.);
    grid2 = tile(uv + vec2(cos(iTime),sin(iTime))*0.02 ,3.);
    color = mix(color, vec3(0.831,0.367,0.133), starPattern(grid2));




    fragColor = vec4(color,1.);
}
