//from https://iquilezles.org/articles/palettes/
vec3 palette( in float t, in vec3 a, in vec3 b, in vec3 c, in vec3 d )
{
    return a + b*cos( 6.28318*(c*t+d) );
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord)/iResolution.xy;


    float val = texture(iChannel0,uv).x;

    vec3 col=palette((1./(maxval))*val,vec3(0.5, 0.5, 0.5),vec3(0.5, 0.5, 0.5),vec3(2.0, 1.0, 0.),vec3(0.50, 0.20, 0.25));
    if(val>maxval)
        col=vec3(1.);
    if(val==0.)
        col=vec3(0.);

    fragColor=vec4(col,1.0);


}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;



    if(abs(uv.x)<(1./iResolution.y)*(scale/2.) && abs(uv.y)<(1./iResolution.y)*(scale/2.)){
        fragColor = vec4(5.0);
    }
    else{

        fragColor=vec4(computePile(iChannel0,fragCoord,iResolution.xy,float(iFrame)));



    }

}

float maxval=4.;
float scale=5.;
float speed=1.;



float computePile(sampler2D channel,vec2 pos,vec2 res, float f){
    float prevUp=texture(channel,(pos+vec2(0.,-scale))/res).x;
    float prevDown=texture(channel,(pos+vec2(0.,scale))/res).x;
    float prevLeft=texture(channel,(pos+vec2(-scale,0.))/res).x;
    float prevRight=texture(channel,(pos+vec2(scale,0.))/res).x;
    float prev=texture(channel,(pos)/res).x;
    float ret=prev;
    if(mod(f,speed)==0.){

        if(prevUp>=maxval)
            ret++;
        if(prevDown>=maxval)
            ret++;
        if(prevLeft>=maxval)
            ret++;
        if(prevRight>=maxval)
            ret++;
        if(prev >= maxval){
            return (prev-maxval);        
        }
        else{
            return (ret);
        }
    }
    else
        return (prev);

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{


    fragColor=vec4(computePile(iChannel0,fragCoord,iResolution.xy,float(iFrame)));


}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{


    fragColor=vec4(computePile(iChannel0,fragCoord,iResolution.xy,float(iFrame)));


}

