void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord.xy-.4*iResolution.xy) / iResolution.y;
    vec2 uv2 = fragCoord/iResolution.xy;
    float wiggle = sin(iTime*5.0+(uv2.y*5.0)) * -0.01;
    vec2 st = vec2(atan(uv.y+wiggle,uv.x+wiggle), length(uv)); // Initialize polar coordinates

    uv = vec2(st.y , st.x); // Use polar coordinates on a cartesian coordinates

    vec2 centralpoint = vec2(uv2.x*1.8f - .7, uv2.y-0.43);
    float ball = length(centralpoint);

    float y = uv.y*10.; // Rise the y coordinates by 10 times
    float e = cos(y)*10.;


    float c = e;

    vec3 mask = vec3(.0, 1., 1.); // Initialize mask to recolor

    vec3 col = vec3(c)*mask;

    fragColor = vec4(1.-col, 1.);

    if(ball<0.3+wiggle)
    fragColor = vec4(1,0,0,1.0);

}
