void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec4 color = texture(iChannel0, uv);

    float contrast = cos(iTime*0.5) + 1.0;
    color.rgb /= color.a;
    color.rgb = ((color.rgb - 0.5) * max(contrast, 0.0)) + 0.5;

    fragColor = color;
}
