// variant of https://shadertoy.com/view/7tV3RV

#define f sin                  // put the trigo func to test here

#define S(v) smoothstep( 1./R.y, 0., v )

void mainImage( out vec4 O, vec2 u )
{
    vec2 R = iResolution.xy,
         U = ( u - .5*R ) / R.y;
    float TAU = 6.28318530718; // indeed, 0718 useless, and 3 can be up to 7
    O-=O;
    vec4 X = U.x + vec4(0,200,400,0)*TAU;    // x + big offsets
    O += S( abs(  U.y - 1e4* ( f(X) - f(mod(X,TAU)) ) )); // plot diffence * 10⁴

    U = abs(fract(10.*U+.5)-.5) / 10.;       // draw grid    
    if (O==vec4(0)) O += S( min(U.x,U.y) );
}
