const float PI = 3.14;
const float e = 2.71;

const float sunRaysHalfDistance = 0.05;
const float sunRadius = 0.3;

const vec4 sunColor = vec4(1., 1., 1., 1.);
const vec4 skyColor = vec4(0.);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = 2. * fragCoord/iResolution.xy - 1.;
    uv.x= uv.x * iResolution.x/iResolution.y;
    uv = uv * (iTime/1. + 0.5);

    vec4 sky = mix(vec4(.8, .8, .8, 1.), skyColor, abs(uv.x) * abs(uv.y)/100.);

    float d = distance(uv, vec2(0.,0.));

    vec4 finalColor = mix(sky, sunColor, pow(e, (-d + sunRadius)/sunRaysHalfDistance));
    fragColor = finalColor;
}
