// 1 for uniform || 0 for const
#define MODE_UNIFORM 1

#if MODE_UNIFORM==1
uniform float x2;
uniform float y2; // should be 0.0 but I'm not sure... (if it is set to 0.0, then I believe the same behavior happens anyway)
#else
const float x2 = 0.0;
const float y2 = 0.0;
#endif

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float x1 = 0.0;
    float y1 = 0.0;

    float p;

    if(fragCoord.y/iResolution.y < 0.5){
        if(fragCoord.x/iResolution.x < 0.5){
            if(fragCoord.x/iResolution.x < 0.25){
                // BOTTOM LEFT
                p = pow(x2, y2); // undefined (flickers)
            }else{
                // BOTTOM MIDDLE LEFT
                p = pow(x1, y2); // 0.0 (black)
            }
        }else{
            if(fragCoord.x/iResolution.x < 0.75){
                // BOTTOM MIDDLE RIGHT
                p = pow(x2, y1); // 1.0 (white)
            }else{
                // BOTTOM RIGHT
                p = pow(x1, y1); // 1.0 (white)
            }
        }
    }else{
        if(fragCoord.x/iResolution.x < 0.5){
            // TOP LEFT
            p = y2; // 0.0
        }else{
            // TOP RIGHT
            p = y1; // 0.0
        }
    }

    // Output to screen
    fragColor = vec4(vec3(p),1.0);
}
