#define PI 3.14159265359

vec3 colorB = vec3(0.104,0.099,0.640);
vec3 colorA = vec3(1.000,0.933,0.000);

float plot (vec2 st, float pct){
  return  smoothstep( pct-0.01, pct, st.y) -
          smoothstep( pct, pct+0.01, st.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 st = fragCoord / min(iResolution.x, iResolution.y) - vec2(0.4, 0.0);
    vec3 color = vec3(0.0);

	float cos_utime = (1. + cos(iTime)) / 2.;
    float sin_utime = (1. + sin(iTime)) / 2.;

    vec3 pct = vec3(clamp(st.y - sin_utime, 0., 1.));

    pct.r = 0.;
    pct.b = sin_utime;

    vec2 c = vec2(0.5, sin_utime);
    vec2 q = st - c;

    float sun = 1.-smoothstep(0.0, .2, length(q));

    color = clamp(mix(colorA, colorB, pct) + sun, vec3(0.), vec3(1.));

    fragColor = vec4(color,1.0);
}
