void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Output to screen
    fragColor = texture(iChannel0, uv);
}

bool brand(float x) {
    return fract(abs(sin(x * 301.238)) * x * 138.83) > 0.5;
}

bool alive(ivec2 c) {
    vec3 t = texelFetch(iChannel0, c, 0).rgb;
    return t.r > 0.0 ||
           t.g > 0.0 ||
           t.b > 0.0;
}

void mainImage( out vec4 C, in vec2 P )
{
    // Rule 110
    // 7 6 5 4 3 2 1 0
    // 0 1 1 0 1 1 1 0
    int c = 0;
    for (int x = -1; x < 2; ++x) {
        // Look below current line
        if (alive(ivec2(int(P.x) - x, int(P.y) - 1))) c += 1 << (1-x);
    }

    bool state = alive(ivec2(P));
    bool alive = false;

    // Not very elegant
    if (c==6||c==5||c==3||c==2||c==1) alive = true;
    //if (c == 0) alive = true;

    vec2 uv = P/iResolution.xy;
    vec3 rainbow = 0.5 + 0.5*cos(iTime*80./iResolution.y+uv.xyx+vec3(0,2,4));
    vec4 col = texelFetch(iChannel0, ivec2(P), 0);

    C = alive ? (!state ? vec4(rainbow, 1.0) : col) : vec4(0.0);
    if (int(P.y) == 0) {
        C = brand(P.x * P.y) ? vec4(1.0) : vec4(0.0);
    }
}
