void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;


    float alpha = 2.0 * (mod(iTime,4.0))/4.0;

    // Output to screen
    fragColor = vec4(0.0);

    fragColor = texture(iChannel0, uv);

    if(uv.x + uv.y > alpha * 2.0)
    {
        fragColor = texture(iChannel1, uv);
    }
    else
    {
        vec2 uv2 = uv * 2.0;

        if(uv2.x + uv2.y > alpha * 2.0)
        {
            vec2 uv3 = alpha - uv;

            if(uv3.x < 0.0)
            {
                //fragColor = vec4(1.0);
                fragColor = texture(iChannel1, uv);
            }
            else if(uv3.y < 0.0)
            {
                //fragColor = vec4(1.0);
                fragColor = texture(iChannel1, uv);
            }            
            else
            {
                fragColor = texture(iChannel1, uv3);
            }

        }
    }
}
