// https://www.desmos.com/calculator/qu0znomrza
float zigzag_wave ( float amp, float freq, float x ) {
    float displacement = -amp / 2. + 0.5;
    float shift = amp > 0. ? (0.5 / freq) : 0.;
    float line_1 = amp * fract( -freq * ( x - shift ) );
    float line_2 = amp * fract(  freq * ( x - shift ) );
    return max( line_1, line_2 ) + displacement;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float t = iTime * 2.;

    float frequency = 4.;

    vec3 col = vec3( zigzag_wave( sin(t), frequency, uv.x ) );
    //col += vec3( zigzag_wave( cos(t), frequency, uv.y ) );
    //col = col / 2.;




    // Time varying pixel color
    vec3 rainbow = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
    col = col * rainbow;

    // Output to screen
    fragColor = vec4(col,1.0);
}
