    const float speed = 5.0;
    const float frequency = 50.;
    const vec3 colorBars = vec3(.25,.45,.33);
    const vec3 colorBarsTwo = vec3(1.,.25,.33);
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{


    // Normalized pixel coordinates (from 0 to 1)

  vec2 uv = fragCoord/iResolution.xy;


    //blurrly vertical lines
    float t = sin(uv.x * frequency + iTime * speed)/2. +.5;

    //Sharp vertical lines
    //float t = step(.5, sin(uv.x * frequency + iTime)/2. +.5);

     vec3 col =  mix(colorBarsTwo, colorBars, t);

    // Output to screen
    fragColor = vec4(col, 1.0);
}
