#define ITERS 12
const float scale = -1.3;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(0.0);
    vec3 col=vec3(0.0),col_prev=vec3(0.0);
    vec2 uv = (fragCoord*10.0-iResolution.xy)/iResolution.y/9.0;
    float s1 = (uv.y+iTime/2.)/8.;
    for(int c=0;c<ITERS;c++){
        col_prev = col;
        for(int i=0;i<ITERS;i++)
        {

            uv.y /= scale;
            uv = (uv+vec2(s1+uv.x-uv.y)/scale);
            //uv.y -= col.x/16.;
            uv.x *= -scale;

            uv = fract(uv.yx);
        }
        col[2] = abs(uv.y-uv.x);
        col = ((col+col_prev.yzx))/1.75;
	}
    fragColor = vec4(vec3(col),1.0);

}
