  float K(float x, float y, float r){
    return x*x+y*y-r;
  }
  float E(float x, float y ,float a,float b, float r){
    return (x/a*x/a)+(y/b*y/b)-r;
  }

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  vec2 p=1.0-2.0*fragCoord.xy/iResolution.xy;
    float x=p.x * iResolution.x/iResolution.y;
    float y=p.y;

      float K1=K(x,y,0.25);
      float E1=E(x-0.2,y+0.5,0.15,0.4,1.);
      float E2=E(x+0.2,y+0.5,0.15,0.4,1.);
      float E3=E(x-0.25,y-0.5,0.25,0.1,1.);
      float E4=E(x+0.25,y-0.5,0.25,0.1,1.);
      float E5=E(x+0.5,y-0.1,0.1,0.2,1.);
      float E6=E(x-0.5,y-0.1,0.1,0.2,1.);

        float z=K1;
        z=min(z,E1);
        z=min(z,E2);
        z=min(z,E3);
        z=min(z,E4);
        z=min(z,E5);
        z=min(z,E6);
          float f=1.25/z;
      vec3 col=vec3(1.,1.,1.);
          if(f<0.) col.r=f; 
          else f=0.;
            fragColor = vec4(col, 1.0 );   
}
