#define maxIter 256.
#define maxNorm 25.


// Color palette
vec3 color(float v) {
    vec3 co;
    if(v == 1.)       co = vec3(25, 7, 26);
    else if(v == 2.)  co = vec3(9, 1, 47);
    else if(v == 3.)  co = vec3(4, 4, 73);
    else if(v == 4.)  co = vec3(0, 7, 100);
    else if(v == 5.)  co = vec3(12, 44, 138);
    else if(v == 6.)  co = vec3(24, 82, 177);
    else if(v == 7.)  co = vec3(57, 125, 209);
    else if(v == 8.)  co = vec3(134, 181, 229);
    else if(v == 9.)  co = vec3(211, 236, 248);
    else if(v == 10.) co = vec3(241, 233, 191);
    else if(v == 11.) co = vec3(248, 201, 95);
    else if(v == 12.) co = vec3(255, 170, 0);
    else if(v == 13.) co = vec3(204, 128, 0);
   	else if(v == 14.) co = vec3(153, 87, 0);
    else if(v == 15.) co = vec3(106, 52, 3);
    return co / 256.0;
}

// Complex multiplication
vec2 cMul(vec2 a, vec2 b) {
    vec2 m = vec2(a.x*b.x - a.y*b.y, a.x*b.y + a.y*b.x);
    return m;
}

// Iterate julia sequence zÂ² + c
float iterJulia(vec2 uv, vec2 c) { 
    float i = 0.;
    vec2 z = uv; // u_0

    for (i = 0.; i<maxIter; ++i) {
        if (dot(z,z) > maxNorm) break;
        z = cMul(z, z) + c;
    }

    return i;
}


void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord/iResolution.xy; // Normalize
    uv -= 0.5; // Center
    uv.x *= iResolution.x/iResolution.y;
    uv *= 3.;

    // Mouse coord
    vec2 c = iMouse.xy/iResolution.xy*2.5;
    c -= 2.5/2.;
    c.x *= iResolution.x/iResolution.y;

    float d = iterJulia(uv, c);
    vec3 col = color(mod(d, 16.));

    fragColor = vec4(col, 1.);
}
