#define myPi 3.141592654f
#define Eps 1e-8
#define maxZ 100.0

const int n = 4;
  vec3 colVec[n] = vec3[n](vec3(1 , 1, 0.7), vec3(1, 0.8, 0.7), vec3(1, 0, 1),vec3(0,1,1));

//float zVec[n] = float[n](3.0, 2.0, 1.0);

int sizes[n] = int[n](3, 4, 4, 3), inds[n] = int[n](0, 3, 7, 11);

const int m = 14;
vec2 polygonsXY[m]; float polygonsZ[m];

void initPolygons(out vec2 polygonsXY[m], out float  polygonsZ[m], in float time){
// 0 -- triangle
    polygonsXY[0] = vec2(0.02, 0.2); polygonsZ[0] = 1.0; 
    polygonsXY[1] = vec2(0.3, 0.1+0.05*cos(time*6.0)); polygonsZ[1] = 1.0;
    polygonsXY[2] = vec2(0.05, 0.9); polygonsZ[2] = 1.0;
// 1 -- quadrilateral
    polygonsXY[3] = vec2(0.25+0.1*sin(time), 0.3+0.1*cos(time)); polygonsZ[3] = 0.1;
    polygonsXY[4] = vec2(0.45+0.1*sin(time), 0.2+0.1*cos(time)); polygonsZ[4] = 0.1;
    polygonsXY[5] = vec2(0.75+0.1*sin(time), 0.5+0.1*cos(time)); polygonsZ[5] = 0.1;
    polygonsXY[6] = vec2(0.65+0.1*sin(time), 0.8+0.1*cos(time)); polygonsZ[6] = 0.1;

// 2 -- rotated square
    float w = 1.0;
    vec3 center = vec3(0.5, 0.3, 0.1 + 0.05*cos(time*w)); float r = 0.2;
    int i; float j =0.0, phi = myPi/4.0;
    float ang = 2.0*myPi/4.0; 
    vec3 tmpVec;
    mat3 rot = mat3(vec3(2.0, 0.0, 0.0), vec3(1.0, cos(phi), sin(phi)), vec3(0.0, -sin(phi), cos(phi)) );
    for (i = 7; i < 11; i++){
        tmpVec = rot*(r*vec3(cos(j*ang + time), sin(j*ang + time), 0.0)); 
        polygonsXY[i] = tmpVec.xy + center.xy;
        polygonsZ[i] = tmpVec.z + center.z;
        j = j + 1.0;
    }
    w=3.0;
    center=vec3(0.5,0.5+0.2*cos(time*w),0);
    r=0.2;
    j=0.4;
    phi=myPi/2.0;
    ang=myPi/2.0;
    for (i = 11; i < 14; i++){
        tmpVec = rot*(r*vec3(cos(j*ang + time), sin(j*ang + time), 0.0)); 
        polygonsXY[i] = tmpVec.xy + center.xy;
        polygonsZ[i] = tmpVec.z + center.z;
        j = j + 1.0;
    }



}



int leftTurn(in vec2 a, in vec2 b, in vec2 c)
{
    float tmp = (c.x - a.x)*(b.y - a.y) - (c.y - a.y)*(b.x - a.x);
    if (abs(tmp) < Eps) 
        return 0;
    else 
        return tmp > 0.0 ? 1: -1;
}


bool inPolygon(in int ind0, in int size, in vec2 vert[m], in vec2 p) // vertices in counter clockwise order
{
    bool res = leftTurn(vert[ind0 + size-1], vert[ind0], p) <= 0; int i = ind0;
    while(res && (i < ind0 + size -1))
    {
        res = res && (leftTurn(vert[i], vert[i+1], p) <= 0); i++;
    }
    return res;
}

float zInPlane(in vec3 a, in vec3 b, in vec3 c, in vec2 p) // z coordinate for (x,y) point of a plane (a-b, c-b)
{
    vec3 nrm = cross(a-b, c-b);
    //float D = - dot(nrm, b);
    if (abs(nrm.z) < Eps) return a.z;
    else return (dot(nrm, b) - nrm.x*p.x - nrm.y*p.y)/nrm.z;
}


// vec3 normalize(vec3), vec3 cross(vec3, vec3) are predefined


void mainImage( out vec4 fragColor, in vec2 fragCoord)
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;


    float time = iTime, tmp; //iTime;
    initPolygons(polygonsXY, polygonsZ, time);
    //initColors(colVec, time);
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));;
    float z = maxZ; 
    int i;
    for (i =0; i < 2; i++){
        if ( inPolygon(inds[i], sizes[i], polygonsXY, uv) ){
            tmp = zInPlane(vec3(polygonsXY[inds[i]].x, polygonsXY[inds[i]].y, polygonsZ[inds[i]]), \
            vec3(polygonsXY[inds[i]+1].x, polygonsXY[inds[i]+1].y, polygonsZ[inds[i]+1]), \
            vec3(polygonsXY[inds[i]+2].x, polygonsXY[inds[i]+2].y, polygonsZ[inds[i]+2]), uv);
            if (tmp < z){
                z = tmp; col = colVec[i]; 
            }
        }
    }

    if ( inPolygon(inds[2], sizes[2], polygonsXY, uv) ){
            tmp = zInPlane(vec3(polygonsXY[inds[2]].x, polygonsXY[inds[2]].y, polygonsZ[inds[2]]), \
            vec3(polygonsXY[inds[2]+1].x, polygonsXY[inds[2]+1].y, polygonsZ[inds[2]+1]), \
            vec3(polygonsXY[inds[2]+2].x, polygonsXY[inds[2]+2].y, polygonsZ[inds[2]+2]), uv);
            if (tmp < z){
                z = tmp; col = colVec[2]+cos(time)*vec3(0,1,1); 
            }
        }
    for (i =3; i < n; i++){
        if ( inPolygon(inds[i], sizes[i], polygonsXY, uv) ){
            tmp = zInPlane(vec3(polygonsXY[inds[i]].x, polygonsXY[inds[i]].y, polygonsZ[inds[i]]), \
            vec3(polygonsXY[inds[i]+1].x, polygonsXY[inds[i]+1].y, polygonsZ[inds[i]+1]), \
            vec3(polygonsXY[inds[i]+2].x, polygonsXY[inds[i]+2].y, polygonsZ[inds[i]+2]), uv);
            if (tmp < z){
                z = tmp; col = colVec[i]+cos(2.0*time)*vec3(1,0.5,0.5); 
            }
        }
    }


    // Output to screen
    fragColor = vec4(col,1);



}
