vec2 white_noise(vec2 uv) {
    return fract(sin(
                vec2(dot(uv, vec2(12, 53)),
                     dot(uv, vec2(52, 125)))
                ) * 23425.0f);
}

float voronoi2D(vec2 uv) {
    vec2 grid = fract(uv);
    vec2 id = floor(uv);
    float dist = 1.0f;

    for (int i = 0; i <= 2; ++i) {
        for (int j = 0; j <= 2; ++j) {
            vec2 offset = id + vec2(i, j);
            vec2 rand = white_noise(offset);
            float d = length(grid + rand - vec2(i, j));
            dist = min(d, dist);
        }
    }
    return dist;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.y;

    vec3 col = vec3(0);
    col = vec3(voronoi2D(uv * 10.0f));

    fragColor = vec4(col,1.0);
}
