void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float minres = min(iResolution.x, iResolution.y);
    vec2 uv = (fragCoord - .5*iResolution.xy) / minres;
    uv.x *= 3.;
    uv.y *= 3.;
    uv.y += 1.;


    vec3 color = vec3(0.0);

    vec2 pos = -uv;

    float r = length(pos)*2.0;
    float x = -atan(pos.y,pos.x);

    float f = (1. + 0.9 * cos(8.*x))*(1. + 0.1*cos(24.*x))*(0.9 + 0.1*cos(200.*x))*(1. + sin(x));

    color = vec3( 1.-smoothstep(f,f+0.02,r) );   
    vec4 scal = vec4(10., 1., 0., 0.);
    float rand = texture(iChannel0, uv.xy * scal.xy + scal.zw).r;
    vec3 col = vec3(rand-0.4, rand+abs(sin(iTime))-0.2, rand*0.2);

    fragColor = vec4(vec3(color) * col, 1.0);
}
