#define ITR 32
#define EPS 0.001

mat2 rot(float angle){
	float s=sin(angle);
	float c=cos(angle);
	return mat2(c,-s,s,c);
}

float sdSphere(vec3 p){
	return length(p)-1.0;
}

float displacement(vec3 p){
	float h=(sin(p.x)*sin(p.y)*sin(p.z));
	return h*sin(iTime)*2.0;
}

float mainDist(vec3 p){
	p.xy*=rot(iTime);
	p.xz*=rot(iTime);
	float disp=displacement(p);
	float dist=sdSphere(p);
	return disp+dist;
}

vec3 genNormal(vec3 p){
	return normalize(p);
}


