#define PI 3.14159265359
#define TWO_PI 6.28318530718

void mainImage( out vec4 fragColor, vec2 fragCoord )
{
	vec2 uv = vec2( 2.0 * fragCoord - iResolution.xy ) / iResolution.y;

    uv.y *= -1.0;
    //uv.y *= cos(iTime * 1.5) - 2.0;
    uv.x *= cos(iTime * 1.5) - 1.5;

    ///from triangle https://www.shadertoy.com/view/MlySzw/////////////////////////////////////
    int N = 3;
    float a = atan(uv.x,uv.y) + PI,
          r = TWO_PI / float(N),
          d = cos(floor(0.5 + a / r) * r - a) * length(uv);          
    ///from triangle/////////////////////////////////////

    d *= 2.0;

    vec4 addColor = vec4(0.5, 0.2, 0.1, 1.0);    
    //float addColorSpeed = 10.0;
    //float addColorR = cos(iTime * addColorSpeed * uv.x); 
    //float addColorG = cos(iTime * addColorSpeed * uv.y); 
    //float addColorB = cos(iTime * addColorSpeed * uv.y * uv.x);

    //vec4 addColor = vec4(addColorR, 0.2, 0.1, 1.0);
    //vec4 addColor = vec4(0.5, addColorG, 0.1, 1.0);
    //vec4 addColor = vec4(0.5, 0.2, addColorB, 1.0);
    vec4 color = vec4(cos(a) - d) + addColor;

    fragColor = color;
}
