const float n = 3.,
    PI = 3.14;

vec2 rotZ(vec2 point, float angle) {
    float c = cos(angle);
    float s = sin(angle);
    vec2 finalPoint = mat2(c, -s, s, c) * point;
    return finalPoint;
}

void mainImage( out vec4 O, in vec2 u ) {
    vec2 Res = iResolution.xy, 
        U = (2. * u - Res) / Res.y;
    vec4 O1 = vec4(1.);
    vec4 O2 = vec4(1.);
    // float m = (n - 1.) * abs(sin(iTime/n)) + 1.; ai dentro
    float m = n;
    for(float i = 0.; i < m; i += 1.) {
        float angle = i * (2. * PI/ n);
        vec2 rotU = rotZ(U, angle + iTime * i/n);
        O1 *= vec4(min(exp((-abs(rotU.y - rotU.x) + 0.5)/0.4142), 1.));
        O2 *= vec4(min(0.4142/abs(rotU.y - rotU.x), 1.));
    }
    O = mix(O1, O2, abs(sin(iTime)));
}
