#define S(a,b,t) smoothstep(a,b,t)

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    //uv.x *= iResolution.x/iResolution.y;
    vec3 col = vec3(texture(iChannel0, uv));
    fragColor = vec4(col,1.0);
}

float activate(float colorSum, int range, float preColor){

    float result = colorSum;

    return result;
}

vec3 kernel(ivec2 fragCoord){
    vec3 colorSum = vec3(0.);
    vec3 preColor = texelFetch(iChannel1, fragCoord, 0).rgb;
    int range = 1;
    mat3 m3 = mat3(
     1., 2.,1.,
     0., 0.,0.,
     -1., -2.,-1.
    );

    vec3 c = vec3(0.,1.,0);


    for(int i = -range; i < range+1 ; i++ ){
        for(int j = -range; j < range+1 ; j++ ){
            colorSum += texelFetch(iChannel1, fragCoord + ivec2(i,j), 0).rgb * m3[j+1][i+1];
        }
    }
    c = vec3(
        activate(colorSum.r, range, preColor.r)
    //activate(colorSum.g, range, preColor.g),
    //activate(colorSum.b, range, preColor.b)
    );

    return c;
}


vec2 N22(vec2 uv){
    vec3 a = fract(uv.xyx * vec3(213.34,234.34,345.65));
    a += dot(a, a+324.45);
    return fract(vec2(a.x*a.y, a.y*a.z));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec3 col = vec3(0.);
    if(iFrame < 10){
        col = vec3(N22(uv).x);
        col = vec3(texture(iChannel1, uv));
    }else{
        col = kernel(ivec2(fragCoord));
    }

    fragColor = vec4(col,1);
}
