void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    vec2 q = uv - vec2(0.6 , 0.7);

    float r = 0.2 + 0.1 * cos(atan(q.y, q.x) * 10.0 + 20.0 * q.x + (1.0 * iTime));
    col *= smoothstep(r, r + 0.01, length(q));

    r = 0.015;
    r += 0.002 * sin(100.0 * q.y);
    r += exp(-70.0 * uv.y);
    col *= 1.0 - (1.0 - smoothstep(r, r + 0.002, abs(q.x + 0.2 * sin(2.0 * q.y)))) * (1.0 - smoothstep(0.0, 0.1, q.y));

    // Output to screen
    fragColor = vec4(col,1.0);

    // Output to screen
    fragColor = vec4(col,1.0);
}
