vec2 offset(vec2 amp, vec2 freq) { return amp.xy * vec2(cos(freq.x * iTime), sin(freq.y * iTime)); }
vec2 pos(vec2 frag, vec2 offset) { return (frag.xy + offset.xy) / iResolution.xy; }

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 xy = fragCoord.xy / iResolution.xy;

    vec2 amp0 = vec2(100.f, 100.f);
    vec2 freq0 = vec2(1.f, 1.f);

    vec2 amp1 = vec2(200.f, 100.f);
    vec2 freq1 = vec2(-0.5f, 1.1f);

    vec2 pos0 = pos(fragCoord, offset(amp0, freq0));
    vec2 pos1 = pos(fragCoord, offset(amp1, freq1));

    vec4 tex0 = texture(iChannel0, pos0);
    vec4 tex1 = texture(iChannel1, pos1);

    vec4 res = vec4(0.0,0.0,0.0,1.0);

    res.r = mix(tex0.x, tex1.x, 0.5);
    res.g = res.r;
    res.b = res.r;


    fragColor = res; 
}
