float edge(vec2 pos, vec2 p1, vec2 p2) {
    vec2 delta = p2 - p1;
    return clamp((dot(pos, delta) - dot(p1, delta)) / dot(delta, delta), 0.0, 1.0);
}

#define P1 vec2(0.5, 0.5)
#define P2 vec2(0.8, -0.5)
#define P3 vec2(-0.8, -0.5)
#define P4 vec2(-0.5, 0.5)
#define P5 vec2(0.0, 0.0)

#define P1V vec3(0.0, 1.0, 1.0)
#define P2V vec3(1.0, 0.0, 0.0)
#define P3V vec3(0.0, 1.0, 0.0)
#define P4V vec3(0.0, 0.0, 1.0)
#define P5V vec3(1.0, 1.0, 0.0)

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (2.*fragCoord - iResolution.xy) / iResolution.y;

    float t1 = edge(uv, P1, P2);
    vec2 p1 = mix(P1, P2, t1);
    vec3 p1v = mix(P1V, P2V, t1);
    float d1 = distance(p1, uv) + 1e-4f;

    float t2 = edge(uv, P2, P3);
    vec2 p2 = mix(P2, P3, t2);
    vec3 p2v = mix(P2V, P3V, t2);
    float d2 = distance(p2, uv) + 1e-4f;

    float t3 = edge(uv, P3, P4);
    vec2 p3 = mix(P3, P4, t3);
    vec3 p3v = mix(P3V, P4V, t3);
    float d3 = distance(p3, uv) + 1e-4f;

    float t4 = edge(uv, P4, P5);
    vec2 p4 = mix(P4, P5, t4);
    vec3 p4v = mix(P4V, P5V, t4);
    float d4 = distance(p4, uv) + 1e-4f;

    float t5 = edge(uv, P5, P1);
    vec2 p5 = mix(P5, P1, t5);
    vec3 p5v = mix(P5V, P1V, t5);
    float d5 = distance(p5, uv) + 1e-4f;

    float sdf = min(min(min(d1, d2), min(d3, d4)), d5);

    d1 *= d1;
    d2 *= d2;
    d3 *= d3;
    d4 *= d4;
    d5 *= d5;

    vec3 col = vec3(abs(cos(sdf * 100.0)) * (0.8 + exp(-100.0*sdf) + 0.3*exp(-5.0*sdf)));
    col *= (p1v / d1 + p2v / d2 + p3v / d3 + p4v / d4 + p5v / d5) / (1. / d1 + 1. / d2 + 1. / d3 + 1. / d4 + 1. / d5);

    // Output to screen
    fragColor = vec4(col,1.0);
}
