void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 R = iResolution.xy,
         uv = fragCoord/R.xy,
         st = (fragCoord-.5*R.xy)/R.y;
         st.x *= iResolution.y/iResolution.x;
    float c = sin(iTime - atan(st.x, st.y) * 3.) - sin(iTime - length(st* 2.) * 7.);
    vec3 col = texture(iChannel0, vec2(fract(c*sin(iTime*.01)), c)).rgb;
    fragColor = vec4(col*2.4, 1.);    
}
