void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Output to screen
    fragColor = vec4(texture(iChannel0, uv).rgb, 1.0);
}

float Hash21(vec2 p) {
  p = fract(p*vec2(234.34, 435.345));
  p += dot(p, p+34.23);
  return fract(p.x * p.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec2 px = 1. / iResolution.xy;

    if (iFrame < 3) {
        fragColor = step(0.5, vec4(Hash21(uv)));
    } else {
        float v = texture(iChannel0, uv).r;
        float n = -v;
        for (int y = -1; y <= 1; y++) {
            for (int x = -1; x <= 1; x++) {
                n += texture(iChannel0, vec2(x, y) * px + uv).r;
            }
        }

        if (v > 0.5) {
            if ((n >= 1.5) && (n <= 3.5)) {
                v = 1.;
            } else {
                v = 0.;
            }
        } else {
            if ((n >= 2.5) && (n <= 3.5)) {
                v = 1.;
            } else {
                v = 0.;
            }
        }

        fragColor = vec4(v);
    }
}
