{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 correctedUv = vec2(uv.x*iResolution.x/iResolution.y, uv.y);


    vec4 stars = texture(iChannel1, uv);

    float cloudLightness = texture(iChannel0, uv).r*pow(texture(iChannel0, uv).g, 2.)*0.76+0.03;

    float r = texture(iChannel0, uv+vec2(0.5, 0.5)).r*0.9+0.23;
    float g = texture(iChannel0, uv).a*0.9-0.05;
    float b = texture(iChannel0, uv).b*0.95+0.42;

