}

// noise
float noise(vec2 pos)
{
    pos.x += 100.*seed;
	return fract( sin( dot(pos*0.001 ,vec2(24.12357, 36.789) ) ) * 12345.123);	
}


// blur noise
float smooth_noise(vec2 pos)
{
	return   ( noise(pos + vec2(1,1)) + noise(pos + vec2(1,1)) + noise(pos + vec2(1,1)) + noise(pos + vec2(1,1)) ) / 16.0 		
		   + ( noise(pos + vec2(1,0)) + noise(pos + vec2(-1,0)) + noise(pos + vec2(0,1)) + noise(pos + vec2(0,-1)) ) / 8.0 		
    	   + noise(pos) / 4.0;
}


// linear interpolation
float interpolate_noise(vec2 pos)
{
	float	a, b, c, d;

	a = smooth_noise(floor(pos));	
	b = smooth_noise(vec2(floor(pos.x+1.0), floor(pos.y)));
	c = smooth_noise(vec2(floor(pos.x), floor(pos.y+1.0)));
	d = smooth_noise(vec2(floor(pos.x+1.0), floor(pos.y+1.0)));

	a = mix(a, b, fract(pos.x));
	b = mix(c, d, fract(pos.x));
	a = mix(a, b, fract(pos.y));

	return a;				   	
}



float perlin_noise(vec2 pos)
{
	float	n;

	n = interpolate_noise(pos*0.0625)*0.5;
	n += interpolate_noise(pos*0.125)*0.25;
	n += interpolate_noise(pos*0.025)*0.225;
	n += interpolate_noise(pos*0.05)*0.0625;
	n += interpolate_noise(pos)*0.03125;
	return n;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){

    vec2 uv = fragCoord/iResolution.xy;
    vec2 correctedUv = vec2(uv.x*iResolution.x/iResolution.y, uv.y);

    const float scale = 100.;

    if(iFrame == 0){
        fragColor = vec4(
        perlin_noise(correctedUv*scale), 
        perlin_noise((correctedUv+vec2(3., 0.))*scale),
        perlin_noise((correctedUv+vec2(6., 0.))*scale),
        perlin_noise((correctedUv+vec2(9., 0.))*scale));
    }else{
        fragColor = texture(iChannel0, uv);
    }
}

#define seed 123.
#define TAU 6.28318530718


float hash11(float p)
{
    p += seed;
    p = fract(p * .1031);
    p *= p + 33.33;
    p *= p + p;
    return fract(p);
}

float hash12(vec2 p)
{
    p.x += seed;
	vec3 p3  = fract(vec3(p.xyx) * .1031);
    p3 += dot(p3, p3.yzx + 33.33);
    return fract((p3.x + p3.y) * p3.z);
}

vec2 hash21(float p)
{
    p += seed;
	vec3 p3 = fract(vec3(p) * vec3(.1031, .1030, .0973));
	p3 += dot(p3, p3.yzx + 33.33);
    return fract((p3.xx+p3.yz)*p3.zy);

}

vec2 hash22(vec2 p)
{
    p.x += seed;
	vec3 p3 = fract(vec3(p.xyx) * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yzx+33.33);
    return fract((p3.xx+p3.yz)*p3.zy);

}

float hash13(vec3 p3)
{
    p3.x += seed;
	p3  = fract(p3 * .1031);
    p3 += dot(p3, p3.zyx + 31.32);
    return fract((p3.x + p3.y) * p3.z);
}

float dist(vec2 p1,vec2 p2){
    return sqrt(pow(p1.x-p2.x, 2.) + pow(p1.y-p2.y, 2.));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec4 col = texture(iChannel1, uv);

    vec2 correctedUv = vec2(uv.x*iResolution.x/iResolution.y, uv.y);

    float closestDist = 1000000.;


    for(float i = 0.; i < 100.; i++){
        vec2 pos = hash22(vec2(i, iFrame));
        pos.x *= iResolution.x/iResolution.y;

        float d = dist(pos, correctedUv);
        if(d < closestDist){
            closestDist = d;
        }

        float r = hash12(vec2(i, iFrame));
        float r2 = hash12(vec2(i+0.5, iFrame));

        float starBrightness = pow(r, 100.)*r2*texture(iChannel0, correctedUv).r*r2;


        float starPixelBrightess = pow(d, -2.)*starBrightness*5e-5;



        if(starPixelBrightess < 0.003) starPixelBrightess = 0.;


        vec3 starRgb = vec3(1.);
        float alpha = pow(hash13(vec3(i, iFrame, 4.)), 0.8);

        r = hash13(vec3(i, iFrame, 0.));

        if(r < 0.04){
            starRgb = vec3(
                0.9+hash13(vec3(i, iFrame, 1.))*0.1,
                0.23+hash13(vec3(i, iFrame, 2.))*0.8,
                0.23+hash13(vec3(i, iFrame, 3.))*0.04
            );
            alpha = pow(alpha, 2.5);
        }else if(r < 0.07){
             starRgb = vec3(
                0.31+hash13(vec3(i, iFrame, 1.))*0.41,
                0.31+hash13(vec3(i, iFrame, 2.))*0.41,
                0.9+hash13(vec3(i, iFrame, 3.))*0.1
             );
             alpha = pow(alpha, 1.2);
        }

        alpha = alpha*0.8+0.2*texture(iChannel0, correctedUv).r;

        vec4 starColor = vec4(starRgb, alpha);

        col += starColor*starPixelBrightess*texture(iChannel0, correctedUv).r;
    }


    // Output to screen
    fragColor = col;
}
