//定义圆
#define t iTime
#define cont 100
float circle(vec2 uv,float radius,vec2 position){
    float c;
    float d = length(uv-position);
    if(d<radius)c=1.;
    return c;
}
//定义RGB颜色
vec3 rgb(float r, float g, float b) {
	return vec3(r / 255.0, g / 255.0, b / 255.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 col;
    float[cont] xn;
    float[cont] yn;
 for(int i = 0 ; i<cont ;i++){
 if(i!=0){
 xn[i]=xn[i-1]+cos(t*0.01*float(i))*float(i)*0.005;
 yn[i]=yn[i-1]+sin(t*0.04*float(i))*float(i)*0.003;
 }else{
 xn[0]=0.;
 yn[0]=0.; 
 }
 }
    vec2 uv = fragCoord/iResolution.xy;//单位化坐标0——1
    uv-=0.5;
    uv.x=uv.x*iResolution.x/iResolution.y;//矫正X轴拉伸
    //col=circle(uv,0.01,vec2(xn[0],yn[0]))*rgb(255.,255.,255.);
    for(int i = 1 ; i <cont;i++){
    col+=circle(uv,0.005,vec2(xn[i],yn[i]))*rgb(255.,0.,0.);   
    }
    fragColor = vec4(col,1.0);     
}
