void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    // sample texture and output to screen
    float res=60.;


    vec2 c=mod(uv,1./res)*res;
    vec2 i=floor(uv*res)/res;
    vec4 tex = texture(iChannel0, i);

    float r=abs(tan(c.x*9.42477796077))*(0.5-abs(c.y-0.5));

    /*
    vec3 tone=vec3(0.,0.,0.);
    if(c.x>0.2 && c.x<0.75){
        tone.g=tex.g;
    }
    if(c.x<0.4){
        tone.r=tex.r;
    }
    if(c.x>0.6){
        tone.b=tex.b;
    }*/

    vec3 tone=vec3(tex.r,0.,0.);
    if(c.x>0.66){
    tone=vec3(0.,tex.g,0.);
    }else if(c.x>0.33){
    tone=vec3(0.,0.,tex.b);
    }

    r=min(floor(r+0.97),1.0);


    // Time varying pixel color
    vec3 col = tone*r; //vec3(r);//

    // Output to screen
    fragColor = vec4(col,1.0);
}
