void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec2 res = 2.0/iResolution.xy;//iMouse.x/iResolution.x;
    vec4 diffused_value = 
        texture(iChannel0, uv.xy + vec2(-res.x, -res.y)) * 1.0/16.0 +
        texture(iChannel0, uv.xy + vec2(0.0, -res.y)) * 1.0/8.0 +
        texture(iChannel0, uv.xy + vec2(res.x, -res.y)) * 1.0/16.0 +
        texture(iChannel0, uv.xy + vec2(-res.x, 0.0)) * 1.0/8.0 +
        texture(iChannel0, uv.xy + vec2(0.0, 0.0)) * 1.0/4.0 +
        texture(iChannel0, uv.xy + vec2(res.x, 0.0)) * 1.0/8.0 +
        texture(iChannel0, uv.xy + vec2(-res.x, res.y)) * 1.0/16.0 +
        texture(iChannel0, uv.xy + vec2(0.0, res.y)) * 1.0/8.0 +
        texture(iChannel0, uv.xy + vec2(res.x, res.y)) * 1.0/16.0;

    fragColor = diffused_value;
}
