float blurryalpha(vec2 uv) {
    // TODO: This, but correctly?
    float sum = 0.0;
    float div = 0.0;
    for (int i = 2; i < 11; ++i) {
        float scale = pow(1.40, float(i));
        float bias = float(i);
        sum += texture(iChannel0, uv, float(i)).a * scale;
        div += scale;
    }
    return sum / div;
}

vec2 alphagradient(vec2 uv, float eps) {
    vec3 o = vec3(-eps, eps, 0.0);
    float lf = blurryalpha(uv + o.xz);
    float rt = blurryalpha(uv + o.yz);
    float up = blurryalpha(uv + o.zx);
    float dn = blurryalpha(uv + o.zy);
    return vec2(rt - lf, dn - up) / eps;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec3 bkg = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    vec4 colour = textureLod(iChannel0, uv, 0.0);
    for (int i = 0; i < 10; ++i) {
        if (colour.a >= 0.95) break;
        vec2 dir = alphagradient(uv, 0.02);
        if (length(dir) < 0.001) dir = alphagradient(uv, 0.1);
        // TODO: if length(dir) is still zeroish then add some noise

        // Use the current blurred alpha value as an estimation of
        // the distance to opaque data.
        float alpha = blurryalpha(uv);

        // Distort it a bit because I don't really know.
        alpha = pow(alpha, 1.8);
#if 1
        // Guess how far out to reach to get in-fill data.
        float scale = 0.075 * (1.0 - alpha);
#else
        // Different guess how far out to reach to get in-fill data.
        float scale = 0.125 / (alpha + 0.3);
#endif

        // Try a little harder to escape as we get more desperate.
        scale += pow(1.01, float(i)) - 1.0;

        // move texture coordinate in direction of increasing alpha,
        // by a distance inferred above.
        uv += scale * normalize(dir);

        // and try again...
#if 0
        colour = textureLod(iChannel0, uv, 0.0);
#else
        vec4 update = textureLod(iChannel0, uv, 0.0);
        colour += update.a * (update - colour);
#endif
    }

    bkg = bkg * (1.0 - colour.a) + colour.rgb * colour.a;

    // Output to screen
    fragColor = vec4(bkg,1.0);
}

float getStencil(vec2 uv) {
    vec3 col = texture(iChannel1, uv).rgb;
    return smoothstep(0.2, 0.35, col.g - max(col.r, col.b));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;
    vec4 colour = texture(iChannel0, uv);

    colour.a = getStencil(uv);

    fragColor = colour;
}
